// contracts/GLDToken.sol
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/security/Pausable.sol";
import "./WithFee.sol";

import "hardhat/console.sol";

contract Token is ERC20, AccessControl, WithFee, Pausable {
    bytes32 public constant MINTER_ROLE = keccak256("MINTER_ROLE");
    bytes32 public constant MAINTAINER_ROLE = keccak256("MAINTAINER_ROLE");
    bytes32 public constant PAUSER_ROLE = keccak256("PAUSER_ROLE");

    address feeCollector;

    constructor(
        string memory name,
        string memory symbol,
        uint256 initialSupply,
        uint256 transferFee,
        uint256 mintingFee,
        address _feeCollector,
        FeeType transferFeeType,
        FeeType mintingFeeType
    )
        ERC20(name, symbol)
        WithFee(transferFee, mintingFee, transferFeeType, mintingFeeType)
    {
        require(_feeCollector != address(0), "feeCollector is zero address");

        _setupRole(DEFAULT_ADMIN_ROLE, msg.sender);
        _mint(msg.sender, initialSupply);
        feeCollector = _feeCollector;
    }

    function decimals() public view virtual override returns (uint8) {
        return 18;
    }

    function mint(address to, uint256 amount) public onlyRole(MINTER_ROLE) {
        uint256 mintingFee = _calculateMintingFee(amount);
        amount -= mintingFee;
        _mint(feeCollector, mintingFee);
        _mint(to, amount);
    }

    function transfer(address to, uint256 amount)
        public
        override
        returns (bool)
    {
        address owner = _msgSender();
        uint256 transferFee = _calculateTransferFee(amount);

        amount -= transferFee;

        _transfer(owner, feeCollector, transferFee);
        _transfer(owner, to, amount);

        return true;
    }

    function setFeeCollector(address _feeCollector)
        external
        onlyRole(MAINTAINER_ROLE)
    {
        feeCollector = _feeCollector;
    }

    function pause() external onlyRole(PAUSER_ROLE) {
        _pause();
    }

    function unpause() external onlyRole(PAUSER_ROLE) {
        _unpause();
    }

    function _beforeTokenTransfer(
        address from,
        address to,
        uint256 amount
    ) internal override whenNotPaused {
        super._beforeTokenTransfer(from, to, amount);
    }

    function calculateMintingFee(uint256 amount)
        external
        view
        returns (uint256)
    {
        return _calculateMintingFee(amount);
    }
}

contracts/Swap.sol.content:
pragma solidity ^0.8.0;

import "@uniswap/v3-periphery/contracts/interfaces/ISwapRouter.sol";
import "@uniswap/v3-periphery/contracts/interfaces/IQuoter.sol";
import "@uniswap/v3-periphery/contracts/libraries/TransferHelper.sol";
import "./IWETH9.sol";

import "hardhat/console.sol";

contract Swap {
    ISwapRouter public immutable swapRouter;
    IQuoter public immutable quoter;
    address beneficiary;
    address public immutable WETH9;

    uint24 public poolFee;

    constructor(
        ISwapRouter _swapRouter,
        IQuoter _quoter,
        address _weth9,
        uint24 _poolFee
    ) {
        swapRouter = _swapRouter;
        quoter = _quoter;
        WETH9 = _weth9;
        poolFee = _poolFee;
    }

    function swapETH(address toToken) external payable returns (uint256) {
        ISwapRouter.ExactInputSingleParams memory params = ISwapRouter
            .ExactInputSingleParams({
                tokenIn: WETH9,
                tokenOut: toToken,
                fee: poolFee,
                recipient: msg.sender,
                deadline: block.timestamp,
                amountIn: msg.value,
                amountOutMinimum: 0,
                sqrtPriceLimitX96: 0
            });

        return swapRouter.exactInputSingle{value: msg.value}(params);
    }

    function estimateETHSwap(uint256 amount, address toToken)
        external
        returns (uint256)
    {
        address tokenIn = WETH9;
        address tokenOut = toToken;
        uint24 fee = 3000;
        uint160 sqrtPriceLimitX96 = 0;

        return
            quoter.quoteExactInputSingle(
                tokenIn,
                tokenOut,
                fee,
                amount,
                sqrtPriceLimitX96
            );
    }
}

